----Create Database
CREATE DATABASE PayrollSystem;
GO

----Use PAYROLL DATABASE
USE PayrollSystem;
GO

----Create schema for easy to query
CREATE SCHEMA GroupProject;
GO

---Drop table area
DROP TABLE IF EXISTS GroupProject.Employee;
DROP TABLE IF EXISTS GroupProject.Attendance;
DROP TABLE IF EXISTS GroupProject.Absence;
DROP TABLE IF EXISTS GroupProject.Saving;
DROP TABLE IF EXISTS GroupProject.Department;
DROP TABLE IF EXISTS GroupProject.Tax;
DROP TABLE IF EXISTS GroupProject.Saving;
DROP TABLE IF EXISTS GroupProject.Payroll;
GO

------Our team create table
CREATE TABLE GroupProject.Department (
	Dept_ID  INT  PRIMARY KEY, 
	Dept_Name VARCHAR (50),
	Salary_Range VARCHAR (50)
);

CREATE TABLE GroupProject.Employee (
	Emp_ID INT PRIMARY KEY,
	Dept_ID  INT,
	First_Name VARCHAR(50),
	Last_Name VARCHAR(50),
	Phone_Number VARCHAR(20),
	Email VARCHAR(100),
	Job_Title VARCHAR(50),
	Gross_Pay DECIMAL(10,2),
	FOREIGN KEY (Dept_ID) REFERENCES GroupProject.Department(Dept_ID)
); 

CREATE TABLE GroupProject.Attendance (
	Attendance_ID INT PRIMARY KEY,
	Emp_ID INT,
	Clockin_Time DATETIME,
	Clockout_Time DATETIME,
	Breaktime_Start DATETIME,
	Breaktime_End DATETIME,
	FOREIGN KEY (Emp_ID) REFERENCES GroupProject.Employee (Emp_ID)

);
	
CREATE TABLE GroupProject.Absence (
	Abs_ID INT PRIMARY KEY,
	Emp_ID INT,
	Date_Start DATE,
	Date_End DATE,
	Duration INT,
	Reason varchar (255)
	FOREIGN KEY (Emp_ID) REFERENCES GroupProject.Employee (Emp_ID)
);


CREATE TABLE GroupProject.Saving (
	Saving_ID VARCHAR(25) PRIMARY KEY,
	Emp_ID INT,
	Balance DECIMAL,
	Saving_Type VARCHAR (100),
	Interest_Rate DECIMAL (10,2),
	Transaction_Date DATE, -- this one for make sure the account of user is still active or not active/
	Transaction_Time TIME,
	FOREIGN KEY (Emp_ID) REFERENCES GroupProject.Employee (Emp_ID)

); 


CREATE TABLE GroupProject.Tax (
	Tax_ID VARCHAR (25) PRIMARY KEY,
	Tax_Rates DECIMAL (10,2),
	Exemption VARCHAR (25),
	Tax_Form VARCHAR (25)
);


CREATE TABLE GroupProject.Payroll (
	Payroll_ID VARCHAR (25) PRIMARY KEY,
	Total_hour INT,
	Net_Pay INT, -- this one is the amount of total money of an emplopyee after paying tax.
	Report_Date DATE, -- reflect the date on which the payroll data is being reported.
	Emp_ID INT,
	Tax_ID VARCHAR (25),
	FOREIGN KEY (Emp_ID) REFERENCES GroupProject.Employee (Emp_ID),
	FOREIGN KEY (Tax_ID) REFERENCES GroupProject.Tax (Tax_ID)

);



